$(function(){
	$(document).on("click", "#goodsPicList>li", function() {
		var o = $(this).index(),
			e = $("#goodsPicList").find("li").eq(o).find("img").attr("data-src");
		$("#goodsPicList").find("li").removeClass("current");
		$("#goodsPicList").find("li").eq(o).addClass("current");
		$("#J_BigPic").attr("src",e);
	});
	$(document).on("click","#J_goodsSize>li",function(){
		var o=$(this).index();
		$("#J_goodsSize").find("li").removeClass("current");
		$("#J_goodsSize").find('li').eq(o).addClass("current");
	});
});